const anchors=document.querySelectorAll('a[href*="#"]');for(let l of anchors)l.addEventListener("click",function(o){o.preventDefault();o=l.getAttribute("href");document.querySelector(""+o).scrollIntoView({behavior:"smooth",block:"start"})});const scrollBtn=document.querySelector(".scroll-up");window.onscroll=()=>{700<window.scrollY?scrollBtn.classList.remove("scroll-up-active"):scrollBtn.classList.add("scroll-up-active")},scrollBtn.onclick=()=>{window.scrollTo(0,0)};