const navMenu=document.getElementById("nav-menu"),icnMenu=document.querySelector(".menu-icon");icnMenu.addEventListener("click",()=>{icnMenu.classList.toggle("active")?navMenu.classList.add("show-menu"):navMenu.classList.remove("show-menu")});var telInp=$('input[type="tel"]'),bgHeader=(telInp.each(function(){$(this).mask("+7(999) 999-99-99")}),"bg-header");function scrollHeader(){60<=window.scrollY||60<=window.pageYOffset?document.getElementsByTagName("header")[0].classList.add(bgHeader):document.getElementsByTagName("header")[0].classList.remove(bgHeader)}window.addEventListener("scroll",scrollHeader);const scrollShow=document.getElementById("scroll-up");function scrollUp(){550<=window.scrollY?scrollShow.classList.add("show-scroll"):scrollShow.classList.remove("show-scroll")}window.addEventListener("scroll",scrollUp);const sections=document.querySelectorAll("section[id]");function navHighlighter(){let s=window.pageYOffset;sections.forEach(e=>{var n=e.offsetHeight,t=e.offsetTop-50;sectionId=e.getAttribute("id"),s>t&&s<=t+n?document.querySelector(".nav__menu a[href*="+sectionId+"]").classList.add("active-link"):document.querySelector(".nav__menu a[href*="+sectionId+"]").classList.remove("active-link")})}window.addEventListener("scroll",navHighlighter);