const modalBtn=document.querySelectorAll("[data-modal]"),body=document.body,modalClose=document.querySelectorAll(".modal_close"),modal=document.querySelectorAll(".modal");function closeModal(o){o.classList.remove("show"),body.classList.remove("no-scroll")}modalBtn.forEach(o=>{o.addEventListener("click",o=>{o=o.currentTarget.getAttribute("data-modal"),o=document.getElementById(o);o.querySelector(".modal__content").addEventListener("click",o=>{o.stopPropagation()}),o.classList.add("show"),body.classList.add("no-scroll")})}),modalClose.forEach(o=>{o.addEventListener("click",o=>{closeModal(o.target.closets(".modal"))})}),modal.forEach(o=>{o.addEventListener("click",o=>{closeModal(o.target)})});